#!/bin/bash

# Train a RotNet (with a ResNet18 architecture) on training images of CIFAR10.
CUDA_VISIBLE_DEVICES=3 nohup python main.py --exp=CIFAR10_RotNet_ResNet18 &
21840

# Train & evaluate linear object classifiers for the CIFAR task on the feature maps 
# generated by the convolutional layers of the above trained ResNet18-based RotNet model.
CUDA_VISIBLE_DEVICES=3 nohup python main.py --exp=CIFAR100_LinearClassifiers_CIFAR10_RotNet_ResNet18_Features &
16159
